# OneCompartAspirin.py# One compartment model of drug dosage of aspirinimport mathdef OneCompartAspirin(DT = 0.01, simLength = 8):	numIterations = int(simLength/DT) + 1	half_life = 3.2	plasma_volume = 3000	aspirin_in_plasma = 2 * 325 * 1000	elimination_constant = -math.log(0.5) / half_life		elimination = elimination_constant * aspirin_in_plasma	plasma_concentration = aspirin_in_plasma / plasma_volume	plasma_concentration_lst = [plasma_concentration]	t = 0	t_lst = [0]	for i in range(1, numIterations):		t = i * DT		t_lst.append(t)		elimination = elimination_constant * aspirin_in_plasma		aspirin_in_plasma = aspirin_in_plasma - elimination * DT		plasma_concentration = aspirin_in_plasma / plasma_volume		plasma_concentration_lst.append(plasma_concentration)			return t_lst, plasma_concentration_lst