# simplePendulum.py# Model of a simple pendulumimport mathdef simplePendulum(length = 1, angle = math.pi/4, angular_velocity = 0, DT = 0.0001, simLength = 12):          numIterations = int(simLength/DT) + 1                    g = 9.81          angle_change = angular_velocity          angular_acceleration = -g * math.sin(angle)/length          t = 0          timeLst = [0]          angleLst = [angle]          angular_velocityLst = [angular_velocity]          angular_accelerationLst = [angular_acceleration]                    for i in range(1, numIterations):                    t = i * DT                    timeLst.append(t)	                        angle = angle + (angle_change) * DT                    angleLst.append(angle)	                        angular_velocity = angular_velocity + (angular_acceleration) * DT                    angular_velocityLst.append(angular_velocity)	                        angle_change = angular_velocity                    angular_acceleration = -g * math.sin(angle)/length                    angular_accelerationLst.append(angular_acceleration)                              return timeLst, angleLst, angular_velocityLst, angular_accelerationLst